import React, {  useState } from "react";

import API from "../api/api";
import { getToken } from "../utils/auth";

function AddMaterial() {
  const [form, setForm] = useState({ name: "", price: "" });
  
 if (!getToken()) {
  return (
    <div
      style={{
        display: "flex",
        height: "80vh",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        fontSize: "1.5rem",
        color: "#333",
        fontWeight: "bold",
      }}
    >
      <p style={{ backgroundColor: "#ffe0e0", padding: "20px", borderRadius: "8px", boxShadow: "0 0 10px rgba(0,0,0,0.1)" }}>
        ðŸš« Please log in to access this page.
      </p>
    </div>
  );
}
  const handleSubmit = async (e) => {
    e.preventDefault();
    await API.post("/materials", form);
    alert("Material added!");
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Material</h2>
      <input placeholder="Material Name" onChange={e => setForm({ ...form, name: e.target.value })} />
      <input placeholder="Price" onChange={e => setForm({ ...form, price: e.target.value })} />
      <button type="submit">Add</button>
    </form>
  );
}

export default AddMaterial;
