import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // You'll create this CSS file

function App() {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/products');
                setProducts(response.data);
            } catch (err) {
                console.error('Error fetching products:', err);
                setError('Failed to load products. Please try again later.');
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, []);

    if (loading) {
        return <div className="container">Loading products...</div>;
    }

    if (error) {
        return <div className="container error-message">{error}</div>;
    }

    return (
        <div className="App">
            <header className="App-header">
                <h1>Building Materials Store</h1>
            </header>
            <main className="container">
                <h2>Our Products</h2>
                <div className="product-list">
                    {products.map(product => (
                        <div key={product.id} className="product-card">
                            <img src={product.image_url || '/images/placeholder.jpg'} alt={product.name} />
                            <h3>{product.name}</h3>
                            <p className="category">{product.category}</p>
                            <p className="description">{product.description}</p>
                            <p className="price">â‚¹{product.price.toFixed(2)} {product.unit}</p>
                            <button className="add-to-cart-btn">Add to Cart</button>
                        </div>
                    ))}
                </div>
            </main>
            <footer className="App-footer">
                <p>&copy; {new Date().getFullYear()} Building Materials Store. All rights reserved.</p>
            </footer>
        </div>
    );
}

export default App;
