import React, {  useState } from "react";

import API from "../api/api";
import { getToken } from "../utils/auth";

function AddMaterial() {
  const [form, setForm] = useState({ name: "", price: "" });
  
 if (!getToken()) {
 return (
  <div style={{
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "80vh"
  }}>
    <div style={{
      backgroundColor: "#f0f0f0",
      padding: "30px",
      borderRadius: "10px",
      boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
      textAlign: "center"
    }}>
      <h2 style={{ marginBottom: "10px", color: "#333" }}>Please login</h2>
      <p style={{ fontSize: "14px", color: "#666" }}>You must be logged in to access this page.</p>
    </div>
  </div>
);
}
  const handleSubmit = async (e) => {
    e.preventDefault();
    await API.post("/materials", form);
    alert("Material added!");
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Material</h2>
      <input placeholder="Material Name" onChange={e => setForm({ ...form, name: e.target.value })} />
      <input placeholder="Price" onChange={e => setForm({ ...form, price: e.target.value })} />
      <button type="submit">Add</button>
    </form>
  );
}

export default AddMaterial;
