import React, { useState } from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import Login from "./components/Login";
import Register from "./components/Register";
import Materials from "./components/Materials";
import AddMaterial from "./components/AddMaterial";
import { isAuthenticated, logout } from "./utils/auth";
import './App.css';

function App() {
  const [auth, setAuth] = useState(isAuthenticated());

  return (
    <BrowserRouter>
     <nav className="navbar">
  <Link to="/" className="nav-button">Materials</Link>
  <Link to="/add" className="nav-button">Add Material</Link>
  {auth ? (
    <button className="nav-button" onClick={() => { logout(); setAuth(false); }}>Logout</button>
  ) : (
    <>
      <Link to="/login" className="nav-button">Login</Link>
      <Link to="/register" className="nav-button">Register</Link>
    </>
  )}
</nav>

      <Routes>
        <Route path="/" element={<Materials />} />
        <Route path="/add" element={auth ? <AddMaterial /> : <p>Please login</p>} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login onLogin={() => setAuth(true)} />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
