import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Register from './components/Register';
import Login from './components/Login';
import Materials from './components/Materials';
import AddMaterial from './components/AddMaterial';
import { isAuthenticated, logout } from './utils/auth';

function App() {
  const [auth, setAuth] = useState(isAuthenticated());

  return (
    <Router>
      <nav>
        <Link to="/">Materials</Link> |{" "}
        {auth ? (
          <>
            <Link to="/add">Add Material</Link> |{" "}
            <button onClick={() => { logout(); setAuth(false); }}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login">Login</Link> | <Link to="/register">Register</Link>
          </>
        )}
      </nav>

      <Routes>
        <Route path="/" element={<Materials />} />
        <Route path="/add" element={auth ? <AddMaterial /> : <p>Please login</p>} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login onLogin={() => setAuth(true)} />} />
      </Routes>
    </Router>
  );
}

export default App;
